sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {


    },

	//Moving verification logic you wrote in the NeedIt when needed field date Business rule into a Script rule. getContentType()
	//Call the Script Include method from the Business Rule
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var currentDateGDT = new GlideDateTime();
		var testDateGDT = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testDateGDT.before(currentDateGDT)) {
			return true;
		}
		else {
			return false;
		}
	},


	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var currentDateGDT = new GlideDateTime();
		var testDateGDT = new GlideDateTime(strDate);

		//Get Date portion of the currentTimeGDT and WhenNeededGTD
		var currentDate = currentDateGDT.getLocalDate();
		var testDate = testDateGDT.getLocalDate();

	 	//Check if current Date is equal to selected Date in When needed field
		if (currentDate.compareTo(testDate) == 0) {
			return true;
		}
		else {
			return false;
		}	
	},

    type: 'NeedItUtils'
};
